logger = logging.getLogger('wx')


def update_config(config_key, value):
    # 创建Session对象:
    session = mysql_api.get_session()
    session.query(ZhConfig) \
        .filter(ZhConfig.config_key == config_key) \
        .update({"value": value})
    session.commit()
    session.close()


